--  DDL for View EMPLOYEE_VIEW
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "EMPLOYEE_VIEW" ("ID", "NAME", "JOB", "ID_DEPARTMENT", "FIRSTNAME", "LASTNAME", "MIDDLENAME", "PHONE", "EMAIL", "BDAY", "ACTIVE", "VERSION", "DEPARTMENTNAME", "OTD_NAME", "ID_OTD", "DEPARTMENTCODE", "CODE_OTD") AS select a."ID",a."NAME",a."JOB",a."ID_DEPARTMENT",a."FIRSTNAME",a."LASTNAME",a."MIDDLENAME",a."PHONE",a."EMAIL",a."BDAY",a."ACTIVE", a."VERSION", d.name departmentname, d.otd_name, d.id_otd, d.code departmentcode, d.code_otd code_otd from cci_employee a left join 
(select b.*, c.otd_name from department_view b left join c_otd c on b.id_otd = c.id) d on a.id_department = d.id
--------------------------------------------------------
--  DDL for View DEPARTMENT_VIEW
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "DEPARTMENT_VIEW" ("ID", "ID_PARENT", "NAME", "CODE", "ID_OTD", "CODE_OTD") AS SELECT a."ID",a."ID_PARENT",a."NAME",a."CODE",a."ID_OTD", b.code code_otd from CCI_DEPARTMENT a left join C_OTD b on a.id_otd=b.id
--------------------------------------------------------
--  DDL for View CLIENT_VIEW
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "CLIENT_VIEW" ("ID", "NAME", "CITY", "STREET", "CINDEX", "OFFICE", "BUILDING", "PHONE", "CELL", "FAX", "UNP", "OKPO", "BNAME", "BCITY", "BSTREET", "BINDEX", "BOFFICE", "BBUILDING", "ACCOUNT", "BUNP", "EMAIL", "BEMAIL", "CODECOUNTRY", "BCODECOUNTRY", "ADDRESS", "VERSION", "LOCKED", "COUNTRY", "BCOUNTRY") AS SELECT  cl."ID",cl."NAME", cl."CITY",cl."STREET",cl."CINDEX",cl."OFFICE",cl."BUILDING",cl."PHONE",cl."CELL", cl."FAX", cl."UNP",cl."OKPO",cl."BNAME",cl."BCITY",cl."BSTREET",cl."BINDEX",cl."BOFFICE",cl."BBUILDING",cl."ACCOUNT",cl."BUNP",cl."EMAIL",cl."BEMAIL",cl."CODECOUNTRY",cl."BCODECOUNTRY", cl."ADDRESS", cl."VERSION", cl."LOCKED", cc.name as country, cb.name as bcountry  
FROM CCI_CLIENT cl 
LEFT JOIN C_COUNTRY cc ON cl.codecountry = cc.code 
LEFT JOIN C_COUNTRY cb ON cl.bcodecountry = cb.code
